#!/usr/bin/env python3\n\"\"\"\nSetup do Gemini Code - Assistente de Desenvolvimento Total\n\"\"\"\n\nfrom setuptools import setup, find_packages\nfrom pathlib import Path\n\n# LÃª o README\nthis_directory = Path(__file__).parent\nlong_description = (this_directory / \"README.md\").read_text(encoding='utf-8')\n\n# DependÃªncias essenciais (sempre instaladas)\ncore_requirements = [\n    \"google-generativeai>=0.8.0\",\n    \"pandas>=2.0.0\",\n    \"sqlalchemy>=2.0.0\",\n    \"aiofiles>=23.0.0\",\n    \"python-dotenv>=1.0.0\",\n    \"jinja2>=3.1.0\",\n    \"psutil>=5.9.0\",\n    \"watchdog>=3.0.0\",\n    \"gitpython>=3.1.40\",\n    \"pyyaml>=6.0\",\n    \"click>=8.1.0\"\n]\n\nsetup(\n    name=\"gemini-code\",\n    version=\"1.0.0\",\n    author=\"Gemini Code Team\",\n    author_email=\"contato@geminicode.dev\",\n    description=\"ðŸ¤– Assistente de desenvolvimento completo com IA em linguagem natural\",\n    long_description=long_description,\n    long_description_content_type=\"text/markdown\",\n    url=\"https://github.com/seu-usuario/gemini-code\",\n    packages=find_packages(),\n    classifiers=[\n        \"Development Status :: 4 - Beta\",\n        \"Intended Audience :: Developers\",\n        \"License :: OSI Approved :: MIT License\",\n        \"Operating System :: OS Independent\",\n        \"Programming Language :: Python :: 3\",\n        \"Programming Language :: Python :: 3.8\",\n        \"Programming Language :: Python :: 3.9\",\n        \"Programming Language :: Python :: 3.10\",\n        \"Programming Language :: Python :: 3.11\",\n        \"Programming Language :: Python :: 3.12\",\n        \"Topic :: Software Development :: Code Generators\",\n        \"Topic :: Software Development :: Libraries :: Python Modules\",\n        \"Topic :: Scientific/Engineering :: Artificial Intelligence\",\n        \"Natural Language :: Portuguese\",\n    ],\n    python_requires=\">=3.8\",\n    install_requires=core_requirements,\n    extras_require={\n        'full': [\n            # VisualizaÃ§Ã£o e analytics\n            'matplotlib>=3.7.0',\n            'seaborn>=0.12.0', \n            'scipy>=1.10.0',\n            'scikit-learn>=1.3.0',\n            'statsmodels>=0.14.0',\n            'numpy>=1.24.0',\n            \n            # SeguranÃ§a\n            'safety>=3.0.0',\n            'cryptography>=41.0.0',\n            \n            # Database extras\n            'psycopg2-binary>=2.9.0',\n            'PyMySQL>=1.0.0',\n            'aiosqlite>=0.19.0',\n            \n            # File processing\n            'Pillow>=10.0.0',\n            'openpyxl>=3.1.0',\n            \n            # Development\n            'pytest>=7.4.0',\n            'pytest-asyncio>=0.21.0',\n            'black>=23.0.0',\n            'flake8>=6.0.0',\n            'mypy>=1.0.0',\n            \n            # Web\n            'requests>=2.31.0',\n            'aiohttp>=3.9.0',\n            \n            # Utilities\n            'networkx>=3.2.0',\n            'tabulate>=0.9.0',\n            'rich>=13.7.0',\n            'prompt-toolkit>=3.0.0',\n            'pygments>=2.17.0',\n            'markdown>=3.5.0',\n            'alembic>=1.13.0'\n        ],\n        'minimal': core_requirements\n    },\n    entry_points={\n        'console_scripts': [\n            'gemini-code=main:main',\n        ],\n    },\n    include_package_data=True,\n    package_data={\n        'gemini_code': [\n            'templates/*.py',\n            'templates/*.md',\n            'config/*.yaml'\n        ]\n    },\n    keywords=[\n        \"ai\", \"artificial intelligence\", \"code assistant\", \"gemini\", \n        \"nlp\", \"natural language\", \"development\", \"automation\",\n        \"portuguese\", \"brasil\", \"programming\", \"assistant\"\n    ]\n)"